function burgerMenu(){const e=document.querySelector(".burger"),r=document.querySelector(".menu"),t=document.querySelector("body");e.addEventListener("click",(()=>{r.classList.contains("active")?(r.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked")):(r.classList.add("active"),e.classList.add("active-burger"),t.classList.add("locked"))}));document.querySelectorAll(".menu__item").forEach((s=>{s.addEventListener("click",(function(){r.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked")}))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(r.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}function aboutSlider(){if(!document.querySelector(".about"))return null;new Swiper(".about__partners-slider",{spaceBetween:24,slidesPerView:4,autoplay:{delay:2500,disableOnInteraction:!1},navigation:{nextEl:".about__partners-arrow--next",prevEl:".about__partners-arrow--prev"},breakpoints:{320:{spaceBetween:12,slidesPerView:2},767:{spaceBetween:16,slidesPerView:3},992:{spaceBetween:24,slidesPerView:4}}})}function restaurantPicturesSlider(){if(!document.querySelector(".restaurant"))return null;new Swiper(".restaurant__slider ",{spaceBetween:6,slidesPerView:1,autoplay:{delay:2500,disableOnInteraction:!1},navigation:{nextEl:".restaurant__slider-arrow-next",prevEl:".restaurant__slider-arrow-prev"},pagination:{el:".restaurant__slider-paginations"}})}burgerMenu(),window.addEventListener("scroll",fixedNav),aboutSlider(),restaurantPicturesSlider();