function burgerMenu(){const e=document.querySelector(".burger"),t=document.querySelector(".menu"),s=document.querySelector("body");e.addEventListener("click",(()=>{t.classList.contains("active")?(t.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked")):(t.classList.add("active"),e.classList.add("active-burger"),s.classList.add("locked"))}));document.querySelectorAll(".menu__item").forEach((r=>{r.addEventListener("click",(function(){t.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked")}))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(t.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}function aboutSlider(){if(!document.querySelector(".about"))return null;new Swiper(".about__partners-slider",{spaceBetween:24,slidesPerView:4,autoplay:{delay:2500,disableOnInteraction:!1},navigation:{nextEl:".about__partners-arrow--next",prevEl:".about__partners-arrow--prev"},breakpoints:{320:{spaceBetween:12,slidesPerView:2},767:{spaceBetween:16,slidesPerView:3},992:{spaceBetween:24,slidesPerView:4}}})}burgerMenu(),window.addEventListener("scroll",fixedNav),aboutSlider();