function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),t=document.querySelector("body");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active-burger"),t.classList.add("locked"))}));document.querySelectorAll(".menu__item").forEach((r=>{r.addEventListener("click",(function(){s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked")}))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}function aboutSlider(){if(!document.querySelector(".about"))return null;new Swiper(".about__partners-slider",{spaceBetween:24,slidesPerView:4,navigation:{nextEl:".about__partners-arrow--next",prevEl:".about__partners-arrow--prev"},breakpoints:{320:{spaceBetween:12,slidesPerView:2},767:{spaceBetween:16,slidesPerView:3},992:{spaceBetween:24,slidesPerView:4}}})}burgerMenu(),window.addEventListener("scroll",fixedNav),aboutSlider();